// Agent related type definitions

export interface Agent {
  agent_id: string;
  agent_type: string;
  status: 'pending' | 'running' | 'working' | 'blocked' | 'completed' | 'failed' | 'error';
  progress: number;
  message: string;
  parent: string | null;
  children?: string[];
  cursor_pid?: number | null;
  claude_pid?: number | null;
  start_time?: string;
  end_time?: string | null;
  phase_index?: number;
  tmux_session?: string;
}

export interface AgentFinding {
  timestamp: string;
  agent_id: string;
  finding_type: 'issue' | 'solution' | 'insight' | 'recommendation' | 'blocker';
  severity: 'low' | 'medium' | 'high' | 'critical';
  message: string;
  data?: Record<string, any>;
}

export interface AgentProgress {
  timestamp: string;
  status: string;
  message: string;
  progress: number;
}

export interface LogEntry {
  timestamp: string;
  type: 'assistant' | 'tool_call' | 'tool_result' | 'error' | 'system';
  content: string;
  metadata?: {
    tool?: string;
    error?: string;
    [key: string]: any;
  };
}

export interface AgentLogsResponse {
  success: boolean;
  logs: LogEntry[];
  metadata: {
    total_lines: number;
    file_size: number;
    last_modified: string;
  };
}

export interface AgentDetailResponse {
  agent: Agent;
  findings: AgentFinding[];
  progress: AgentProgress[];
  logs: LogEntry[];
}

import React, { useRef, useEffect, useState } from 'react';
import { LogEntry } from '../types/agent';

interface LogViewerProps {
  logs: LogEntry[];
  className?: string;
}

export const LogViewer: React.FC<LogViewerProps> = ({ logs, className = '' }) => {
  const containerRef = useRef<HTMLDivElement>(null);
  const [autoScroll, setAutoScroll] = useState(true);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredLogs, setFilteredLogs] = useState<LogEntry[]>(logs);

  // Filter logs based on search term
  useEffect(() => {
    if (!searchTerm) {
      setFilteredLogs(logs);
    } else {
      const term = searchTerm.toLowerCase();
      setFilteredLogs(logs.filter(log =>
        log.content.toLowerCase().includes(term) ||
        log.type.toLowerCase().includes(term)
      ));
    }
  }, [logs, searchTerm]);

  // Auto-scroll to bottom when new logs arrive
  useEffect(() => {
    if (autoScroll && containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  }, [filteredLogs, autoScroll]);

  const getLogTypeColor = (type: LogEntry['type']) => {
    switch (type) {
      case 'assistant':
        return 'text-blue-400';
      case 'tool_call':
        return 'text-purple-400';
      case 'tool_result':
        return 'text-green-400';
      case 'error':
        return 'text-red-400';
      case 'system':
        return 'text-gray-400';
      default:
        return 'text-gray-300';
    }
  };

  const handleCopy = (content: string) => {
    navigator.clipboard.writeText(content);
    // Could add a toast notification here
  };

  const handleScroll = () => {
    if (!containerRef.current) return;
    const { scrollTop, scrollHeight, clientHeight } = containerRef.current;
    // Check if user is near the bottom (within 50px)
    const isNearBottom = scrollHeight - scrollTop - clientHeight < 50;
    setAutoScroll(isNearBottom);
  };

  const formatTimestamp = (timestamp: string) => {
    const date = new Date(timestamp);
    return date.toLocaleTimeString('en-US', {
      hour12: false,
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
      fractionalSecondDigits: 3
    });
  };

  const formatContent = (content: string) => {
    // Detect and format code blocks
    if (content.includes('```')) {
      return content.split('```').map((part, index) => {
        if (index % 2 === 1) {
          // This is a code block
          const [lang, ...code] = part.split('\n');
          return (
            <pre key={index} className="bg-gray-900 p-2 rounded mt-1 mb-1 overflow-x-auto">
              <code className={`language-${lang || 'plaintext'}`}>
                {code.join('\n')}
              </code>
            </pre>
          );
        }
        return <span key={index}>{part}</span>;
      });
    }
    return content;
  };

  return (
    <div className={`flex flex-col h-full bg-gray-800 rounded-lg ${className}`}>
      {/* Header with search and controls */}
      <div className="flex items-center justify-between p-4 border-b border-gray-700">
        <div className="flex items-center space-x-4">
          <h3 className="text-lg font-semibold text-gray-100">Live Logs</h3>
          <span className="text-sm text-gray-400">
            {filteredLogs.length} entries
          </span>
        </div>

        <div className="flex items-center space-x-4">
          <input
            type="text"
            placeholder="Search logs..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className="px-3 py-1 bg-gray-700 text-gray-100 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
          />

          <button
            onClick={() => setAutoScroll(!autoScroll)}
            className={`px-3 py-1 rounded-md transition-colors ${
              autoScroll
                ? 'bg-blue-600 text-white hover:bg-blue-700'
                : 'bg-gray-700 text-gray-300 hover:bg-gray-600'
            }`}
          >
            {autoScroll ? 'Auto-scroll ON' : 'Auto-scroll OFF'}
          </button>
        </div>
      </div>

      {/* Log container */}
      <div
        ref={containerRef}
        onScroll={handleScroll}
        className="flex-1 overflow-y-auto p-4 space-y-2 font-mono text-sm"
      >
        {filteredLogs.length === 0 ? (
          <div className="text-gray-500 text-center py-8">
            {searchTerm ? 'No logs match your search' : 'No logs available'}
          </div>
        ) : (
          filteredLogs.map((log, index) => (
            <div
              key={`${log.timestamp}-${index}`}
              className="group hover:bg-gray-750 rounded p-2 transition-colors"
            >
              <div className="flex items-start space-x-2">
                {/* Timestamp */}
                <span className="text-gray-500 text-xs whitespace-nowrap">
                  {formatTimestamp(log.timestamp)}
                </span>

                {/* Type badge */}
                <span className={`text-xs font-semibold uppercase ${getLogTypeColor(log.type)}`}>
                  [{log.type}]
                </span>

                {/* Content */}
                <div className="flex-1 text-gray-200 break-words whitespace-pre-wrap">
                  {formatContent(log.content)}
                </div>

                {/* Copy button (shown on hover) */}
                <button
                  onClick={() => handleCopy(log.content)}
                  className="opacity-0 group-hover:opacity-100 transition-opacity p-1 hover:bg-gray-700 rounded"
                  title="Copy log entry"
                >
                  <svg className="w-4 h-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 16H6a2 2 0 01-2-2V6a2 2 0 012-2h8a2 2 0 012 2v2m-6 12h8a2 2 0 002-2v-8a2 2 0 00-2-2h-8a2 2 0 00-2 2v8a2 2 0 002 2z" />
                  </svg>
                </button>
              </div>

              {/* Metadata if present */}
              {log.metadata && Object.keys(log.metadata).length > 0 && (
                <div className="mt-1 ml-20 text-xs text-gray-500">
                  {Object.entries(log.metadata).map(([key, value]) => (
                    <span key={key} className="mr-4">
                      {key}: <span className="text-gray-400">{JSON.stringify(value)}</span>
                    </span>
                  ))}
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

import { useState, useEffect, useCallback } from 'react';
import { LogEntry, AgentLogsResponse } from '../types/agent';

interface UseAgentLogsOptions {
  taskId: string;
  agentId: string;
  pollInterval?: number; // in milliseconds
  enabled?: boolean;
}

interface UseAgentLogsReturn {
  logs: LogEntry[];
  isLoading: boolean;
  error: string | null;
  refetch: () => void;
  metadata: AgentLogsResponse['metadata'] | null;
}

export const useAgentLogs = ({
  taskId,
  agentId,
  pollInterval = 2000, // Default 2 seconds
  enabled = true
}: UseAgentLogsOptions): UseAgentLogsReturn => {
  const [logs, setLogs] = useState<LogEntry[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [metadata, setMetadata] = useState<AgentLogsResponse['metadata'] | null>(null);

  const fetchLogs = useCallback(async () => {
    if (!taskId || !agentId) {
      console.warn('Missing taskId or agentId for fetching logs');
      return;
    }

    try {
      setIsLoading(true);
      setError(null);

      const response = await fetch(`http://localhost:8000/api/agents/${taskId}/${agentId}/logs`);

      if (!response.ok) {
        throw new Error(`Failed to fetch logs: ${response.statusText}`);
      }

      const data: AgentLogsResponse = await response.json();

      if (data.success) {
        // Process logs to ensure they have proper structure
        const processedLogs = data.logs.map(log => ({
          ...log,
          timestamp: log.timestamp || new Date().toISOString(),
          type: log.type || 'system',
          content: log.content || ''
        }));

        setLogs(processedLogs);
        setMetadata(data.metadata);
      } else {
        throw new Error('Failed to fetch logs');
      }
    } catch (err) {
      console.error('Error fetching agent logs:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch logs');
    } finally {
      setIsLoading(false);
    }
  }, [taskId, agentId]);

  // Initial fetch and polling setup
  useEffect(() => {
    if (!enabled) return;

    // Initial fetch
    fetchLogs();

    // Set up polling
    const intervalId = setInterval(() => {
      fetchLogs();
    }, pollInterval);

    // Cleanup
    return () => {
      clearInterval(intervalId);
    };
  }, [fetchLogs, pollInterval, enabled]);

  return {
    logs,
    isLoading,
    error,
    refetch: fetchLogs,
    metadata
  };
};

// Hook for fetching agent details (including status, findings, progress)
interface UseAgentDetailsOptions {
  taskId: string;
  agentId: string;
  pollInterval?: number;
  enabled?: boolean;
}

export const useAgentDetails = ({
  taskId,
  agentId,
  pollInterval = 5000, // Default 5 seconds for status updates
  enabled = true
}: UseAgentDetailsOptions) => {
  const [agent, setAgent] = useState(null);
  const [findings, setFindings] = useState([]);
  const [progress, setProgress] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchDetails = useCallback(async () => {
    if (!taskId || !agentId) return;

    try {
      setIsLoading(true);
      setError(null);

      const response = await fetch(`http://localhost:8000/api/agents/${taskId}/${agentId}`);

      if (!response.ok) {
        throw new Error(`Failed to fetch agent details: ${response.statusText}`);
      }

      const data = await response.json();

      if (data.success) {
        setAgent(data.agent);
        setFindings(data.findings || []);
        setProgress(data.progress || []);
      }
    } catch (err) {
      console.error('Error fetching agent details:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch details');
    } finally {
      setIsLoading(false);
    }
  }, [taskId, agentId]);

  useEffect(() => {
    if (!enabled) return;

    fetchDetails();

    const intervalId = setInterval(() => {
      fetchDetails();
    }, pollInterval);

    return () => {
      clearInterval(intervalId);
    };
  }, [fetchDetails, pollInterval, enabled]);

  return {
    agent,
    findings,
    progress,
    isLoading,
    error,
    refetch: fetchDetails
  };
};

import React, { useState, useEffect } from 'react';
import { useParams, Link } from 'react-router-dom';
import { LogViewer } from '../components/LogViewer';
import { useAgentLogs, useAgentDetails } from '../hooks/useAgentLogs';
import { Agent, AgentFinding, AgentProgress } from '../types/agent';

export const AgentDetail: React.FC = () => {
  const { taskId, agentId } = useParams<{ taskId: string; agentId: string }>();
  const [activeTab, setActiveTab] = useState<'logs' | 'findings' | 'progress'>('logs');

  // Fetch agent details and logs
  const { agent, findings, progress, isLoading: detailsLoading, error: detailsError } = useAgentDetails({
    taskId: taskId || '',
    agentId: agentId || '',
    enabled: !!(taskId && agentId)
  });

  const { logs, isLoading: logsLoading, error: logsError, metadata } = useAgentLogs({
    taskId: taskId || '',
    agentId: agentId || '',
    enabled: !!(taskId && agentId) && activeTab === 'logs'
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'completed':
        return 'text-green-400 bg-green-900/30';
      case 'working':
        return 'text-blue-400 bg-blue-900/30';
      case 'running':
        return 'text-cyan-400 bg-cyan-900/30';
      case 'blocked':
        return 'text-orange-400 bg-orange-900/30';
      case 'failed':
      case 'error':
        return 'text-red-400 bg-red-900/30';
      default:
        return 'text-gray-400 bg-gray-900/30';
    }
  };

  const getSeverityColor = (severity: string) => {
    switch (severity) {
      case 'critical':
        return 'text-red-500 bg-red-900/20 border-red-700';
      case 'high':
        return 'text-orange-500 bg-orange-900/20 border-orange-700';
      case 'medium':
        return 'text-yellow-500 bg-yellow-900/20 border-yellow-700';
      case 'low':
        return 'text-blue-500 bg-blue-900/20 border-blue-700';
      default:
        return 'text-gray-500 bg-gray-900/20 border-gray-700';
    }
  };

  const getFindingIcon = (type: string) => {
    switch (type) {
      case 'issue':
        return '‚ö†Ô∏è';
      case 'solution':
        return '‚úÖ';
      case 'insight':
        return 'üí°';
      case 'recommendation':
        return 'üìù';
      case 'blocker':
        return 'üö´';
      default:
        return 'üìå';
    }
  };

  const calculateDuration = (startTime?: string, endTime?: string | null) => {
    if (!startTime) return 'N/A';
    const start = new Date(startTime).getTime();
    const end = endTime ? new Date(endTime).getTime() : Date.now();
    const duration = end - start;

    const hours = Math.floor(duration / 3600000);
    const minutes = Math.floor((duration % 3600000) / 60000);
    const seconds = Math.floor((duration % 60000) / 1000);

    if (hours > 0) {
      return `${hours}h ${minutes}m ${seconds}s`;
    } else if (minutes > 0) {
      return `${minutes}m ${seconds}s`;
    } else {
      return `${seconds}s`;
    }
  };

  if (!taskId || !agentId) {
    return (
      <div className="min-h-screen bg-gray-900 text-gray-100 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center py-12">
            <h2 className="text-2xl font-bold text-red-400">Invalid Agent Details</h2>
            <p className="text-gray-400 mt-2">Missing task ID or agent ID</p>
            <Link to="/" className="mt-4 inline-block px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-md">
              Back to Dashboard
            </Link>
          </div>
        </div>
      </div>
    );
  }

  if (detailsLoading) {
    return (
      <div className="min-h-screen bg-gray-900 text-gray-100 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center py-12">
            <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto"></div>
            <p className="text-gray-400 mt-4">Loading agent details...</p>
          </div>
        </div>
      </div>
    );
  }

  if (detailsError || !agent) {
    return (
      <div className="min-h-screen bg-gray-900 text-gray-100 p-8">
        <div className="max-w-7xl mx-auto">
          <div className="text-center py-12">
            <h2 className="text-2xl font-bold text-red-400">Error Loading Agent</h2>
            <p className="text-gray-400 mt-2">{detailsError || 'Agent not found'}</p>
            <Link to="/" className="mt-4 inline-block px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-md">
              Back to Dashboard
            </Link>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-gray-100">
      {/* Header */}
      <div className="bg-gray-800 border-b border-gray-700 px-8 py-6">
        <div className="max-w-7xl mx-auto">
          <div className="flex items-start justify-between">
            <div>
              <div className="flex items-center space-x-4 mb-2">
                <Link to="/" className="text-blue-400 hover:text-blue-300">
                  ‚Üê Back to Dashboard
                </Link>
                {agent.parent && (
                  <Link
                    to={`/agent/${taskId}/${agent.parent}`}
                    className="text-gray-400 hover:text-gray-300"
                  >
                    Parent: {agent.parent}
                  </Link>
                )}
              </div>

              <h1 className="text-2xl font-bold text-gray-100">{agent.agent_id}</h1>
              <p className="text-gray-400 mt-1">{agent.agent_type}</p>
            </div>

            <div className="text-right">
              <div className={`inline-flex items-center px-3 py-1 rounded-full text-sm font-semibold ${getStatusColor(agent.status)}`}>
                {agent.status.toUpperCase()}
              </div>
              <div className="text-gray-400 text-sm mt-2">
                Phase {agent.phase_index !== undefined ? agent.phase_index + 1 : 'N/A'}
              </div>
            </div>
          </div>

          {/* Progress Bar and Stats */}
          <div className="mt-6 grid grid-cols-1 md:grid-cols-4 gap-4">
            {/* Progress Bar */}
            <div className="md:col-span-2">
              <div className="flex items-center justify-between mb-2">
                <span className="text-sm text-gray-400">Progress</span>
                <span className="text-sm font-semibold text-gray-300">{agent.progress}%</span>
              </div>
              <div className="w-full bg-gray-700 rounded-full h-3">
                <div
                  className="bg-blue-500 h-3 rounded-full transition-all duration-500"
                  style={{ width: `${agent.progress}%` }}
                />
              </div>
            </div>

            {/* Duration */}
            <div className="bg-gray-800/50 rounded-lg p-3">
              <div className="text-xs text-gray-400 mb-1">Duration</div>
              <div className="text-lg font-semibold text-gray-100">
                {calculateDuration(agent.start_time, agent.end_time)}
              </div>
            </div>

            {/* Process Info */}
            <div className="bg-gray-800/50 rounded-lg p-3">
              <div className="text-xs text-gray-400 mb-1">Process</div>
              <div className="text-sm text-gray-300">
                {agent.cursor_pid ? `PID: ${agent.cursor_pid}` :
                 agent.claude_pid ? `PID: ${agent.claude_pid}` :
                 agent.tmux_session ? `tmux: ${agent.tmux_session}` : 'N/A'}
              </div>
            </div>
          </div>

          {/* Latest message */}
          {agent.message && (
            <div className="mt-4 p-3 bg-gray-800/50 rounded-lg">
              <div className="text-xs text-gray-400 mb-1">Latest Message</div>
              <p className="text-gray-200">{agent.message}</p>
            </div>
          )}
        </div>
      </div>

      {/* Tabs */}
      <div className="bg-gray-800 border-b border-gray-700 px-8">
        <div className="max-w-7xl mx-auto">
          <div className="flex space-x-8">
            <button
              onClick={() => setActiveTab('logs')}
              className={`py-3 px-1 border-b-2 transition-colors ${
                activeTab === 'logs'
                  ? 'border-blue-500 text-blue-400'
                  : 'border-transparent text-gray-400 hover:text-gray-300'
              }`}
            >
              Live Logs
              {metadata && (
                <span className="ml-2 text-xs bg-gray-700 px-2 py-1 rounded-full">
                  {metadata.total_lines}
                </span>
              )}
            </button>

            <button
              onClick={() => setActiveTab('findings')}
              className={`py-3 px-1 border-b-2 transition-colors ${
                activeTab === 'findings'
                  ? 'border-blue-500 text-blue-400'
                  : 'border-transparent text-gray-400 hover:text-gray-300'
              }`}
            >
              Findings
              {findings.length > 0 && (
                <span className="ml-2 text-xs bg-gray-700 px-2 py-1 rounded-full">
                  {findings.length}
                </span>
              )}
            </button>

            <button
              onClick={() => setActiveTab('progress')}
              className={`py-3 px-1 border-b-2 transition-colors ${
                activeTab === 'progress'
                  ? 'border-blue-500 text-blue-400'
                  : 'border-transparent text-gray-400 hover:text-gray-300'
              }`}
            >
              Progress History
              {progress.length > 0 && (
                <span className="ml-2 text-xs bg-gray-700 px-2 py-1 rounded-full">
                  {progress.length}
                </span>
              )}
            </button>
          </div>
        </div>
      </div>

      {/* Content */}
      <div className="p-8">
        <div className="max-w-7xl mx-auto">
          {/* Logs Tab */}
          {activeTab === 'logs' && (
            <div className="h-[calc(100vh-400px)]">
              {logsError ? (
                <div className="text-center py-12">
                  <p className="text-red-400">Error loading logs: {logsError}</p>
                </div>
              ) : (
                <LogViewer logs={logs} className="h-full" />
              )}
            </div>
          )}

          {/* Findings Tab */}
          {activeTab === 'findings' && (
            <div className="space-y-4">
              {findings.length === 0 ? (
                <div className="text-center py-12">
                  <p className="text-gray-500">No findings reported yet</p>
                </div>
              ) : (
                findings.map((finding: AgentFinding, index: number) => (
                  <div
                    key={`${finding.timestamp}-${index}`}
                    className={`border rounded-lg p-4 ${getSeverityColor(finding.severity)}`}
                  >
                    <div className="flex items-start justify-between">
                      <div className="flex items-start space-x-3">
                        <span className="text-2xl">{getFindingIcon(finding.finding_type)}</span>
                        <div className="flex-1">
                          <div className="flex items-center space-x-3 mb-2">
                            <span className="font-semibold uppercase text-sm">
                              {finding.finding_type}
                            </span>
                            <span className="text-xs px-2 py-1 bg-gray-800 rounded">
                              {finding.severity}
                            </span>
                          </div>
                          <p className="text-gray-200">{finding.message}</p>
                          {finding.data && Object.keys(finding.data).length > 0 && (
                            <pre className="mt-3 p-3 bg-gray-900 rounded text-xs overflow-x-auto">
                              {JSON.stringify(finding.data, null, 2)}
                            </pre>
                          )}
                        </div>
                      </div>
                      <span className="text-xs text-gray-500">
                        {new Date(finding.timestamp).toLocaleTimeString()}
                      </span>
                    </div>
                  </div>
                ))
              )}
            </div>
          )}

          {/* Progress Tab */}
          {activeTab === 'progress' && (
            <div className="space-y-2">
              {progress.length === 0 ? (
                <div className="text-center py-12">
                  <p className="text-gray-500">No progress updates yet</p>
                </div>
              ) : (
                <div className="relative">
                  {/* Timeline line */}
                  <div className="absolute left-8 top-0 bottom-0 w-0.5 bg-gray-700"></div>

                  {progress.map((update: AgentProgress, index: number) => (
                    <div key={`${update.timestamp}-${index}`} className="relative flex items-start">
                      {/* Timeline dot */}
                      <div className={`absolute left-7 w-3 h-3 rounded-full border-2 border-gray-700 ${
                        update.status === 'completed' ? 'bg-green-500' :
                        update.status === 'working' ? 'bg-blue-500' :
