// Task and Agent types for the orchestrator dashboard

export interface Task {
  task_id: string;
  description: string;
  priority: string;
  status: 'pending' | 'active' | 'completed' | 'failed';
  created_at: string;
  updated_at: string;
  phases: Phase[];
  agents: Agent[];
  current_phase_index?: number;
  error?: string;
}

export interface Phase {
  name: string;
  description?: string;
  status: 'PENDING' | 'ACTIVE' | 'AWAITING_REVIEW' | 'UNDER_REVIEW' | 'APPROVED' | 'REJECTED' | 'REVISING' | 'ESCALATED';
  agents?: Agent[];
  review_data?: ReviewData;
}

export interface ReviewData {
  review_id?: string;
  reviewer_agents?: string[];
  verdicts_received?: number;
  verdicts_required?: number;
  findings?: Finding[];
  summary?: string;
}

export interface Agent {
  agent_id: string;
  type: string;
  status: 'pending' | 'starting' | 'running' | 'completed' | 'failed' | 'terminated';
  progress: number;
  message?: string;
  created_at: string;
  updated_at: string;
  parent?: string;
  phase_index?: number;
  tmux_session?: string;
  model?: string;
  findings?: Finding[];
}

export interface Finding {
  finding_type: 'issue' | 'solution' | 'insight' | 'recommendation' | 'blocker';
  severity: 'low' | 'medium' | 'high' | 'critical';
  message: string;
  timestamp: string;
  agent_id?: string;
  data?: Record<string, any>;
}

export interface LogMessage {
  timestamp: string;
  level: 'debug' | 'info' | 'warning' | 'error';
  message: string;
  agent_id?: string;
  task_id?: string;
  data?: any;
}

// API service for communicating with the FastAPI backend

import { Task, Agent, Finding, LogMessage } from '../types';

const API_BASE_URL = import.meta.env.VITE_API_URL || 'http://localhost:8000';
const WS_BASE_URL = import.meta.env.VITE_WS_URL || 'ws://localhost:8000';

class ApiService {
  private baseUrl: string;
  private wsBaseUrl: string;

  constructor() {
    this.baseUrl = API_BASE_URL;
    this.wsBaseUrl = WS_BASE_URL;
  }

  // Helper method for fetch with error handling
  private async fetchJson<T>(url: string, options?: RequestInit): Promise<T> {
    const response = await fetch(`${this.baseUrl}${url}`, {
      ...options,
      headers: {
        'Content-Type': 'application/json',
        ...options?.headers,
      },
    });

    if (!response.ok) {
      throw new Error(`API Error: ${response.status} ${response.statusText}`);
    }

    return response.json();
  }

  // Task endpoints
  async getTasks(): Promise<Task[]> {
    return this.fetchJson<Task[]>('/api/tasks');
  }

  async getTask(taskId: string): Promise<Task> {
    return this.fetchJson<Task>(`/api/tasks/${taskId}`);
  }

  async createTask(task: Partial<Task>): Promise<Task> {
    return this.fetchJson<Task>('/api/tasks', {
      method: 'POST',
      body: JSON.stringify(task),
    });
  }

  async updateTask(taskId: string, task: Partial<Task>): Promise<Task> {
    return this.fetchJson<Task>(`/api/tasks/${taskId}`, {
      method: 'PUT',
      body: JSON.stringify(task),
    });
  }

  // Agent endpoints
  async getAgents(taskId: string): Promise<Agent[]> {
    return this.fetchJson<Agent[]>(`/api/agents/${taskId}`);
  }

  async getAgent(taskId: string, agentId: string): Promise<Agent> {
    return this.fetchJson<Agent>(`/api/agents/${taskId}/${agentId}`);
  }

  async getAgentOutput(taskId: string, agentId: string): Promise<string> {
    const response = await fetch(`${this.baseUrl}/api/agents/${taskId}/${agentId}/output`);
    if (!response.ok) {
      throw new Error(`API Error: ${response.status} ${response.statusText}`);
    }
    return response.text();
  }

  async terminateAgent(taskId: string, agentId: string): Promise<void> {
    await this.fetchJson<void>(`/api/agents/${taskId}/${agentId}/terminate`, {
      method: 'POST',
    });
  }

  // Findings endpoints
  async getFindings(taskId: string): Promise<Finding[]> {
    return this.fetchJson<Finding[]>(`/api/findings/${taskId}`);
  }

  // WebSocket connection for real-time logs
  connectToLogs(taskId: string, onMessage: (message: LogMessage) => void, onError?: (error: Event) => void): WebSocket {
    const ws = new WebSocket(`${this.wsBaseUrl}/ws/${taskId}`);

    ws.onmessage = (event) => {
      try {
        const message = JSON.parse(event.data) as LogMessage;
        onMessage(message);
      } catch (error) {
        console.error('Failed to parse WebSocket message:', error);
      }
    };

    ws.onerror = (event) => {
      console.error('WebSocket error:', event);
      if (onError) onError(event);
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
    };

    return ws;
  }

  // tmux session endpoint
  async attachToTmux(sessionName: string): Promise<{ url: string }> {
    return this.fetchJson<{ url: string }>(`/api/tmux/attach/${sessionName}`);
  }
}

export const apiService = new ApiService();

// Zustand store for task and agent state management

import { create } from 'zustand';
import { Task, Agent, Finding, LogMessage } from '../types';
import { apiService } from '../services/api';

interface TaskStore {
  // State
  tasks: Task[];
  selectedTask: Task | null;
  selectedAgent: Agent | null;
  agents: Agent[];
  findings: Finding[];
  logs: LogMessage[];
  loading: boolean;
  error: string | null;

  // Actions
  fetchTasks: () => Promise<void>;
  fetchTask: (taskId: string) => Promise<void>;
  fetchAgents: (taskId: string) => Promise<void>;
  fetchFindings: (taskId: string) => Promise<void>;
  selectTask: (task: Task | null) => void;
  selectAgent: (agent: Agent | null) => void;
  addLog: (log: LogMessage) => void;
  clearLogs: () => void;
  setError: (error: string | null) => void;
}

export const useTaskStore = create<TaskStore>((set, get) => ({
  // Initial state
  tasks: [],
  selectedTask: null,
  selectedAgent: null,
  agents: [],
  findings: [],
  logs: [],
  loading: false,
  error: null,

  // Actions
  fetchTasks: async () => {
    set({ loading: true, error: null });
    try {
      const tasks = await apiService.getTasks();
      set({ tasks, loading: false });
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to fetch tasks',
        loading: false
      });
    }
  },

  fetchTask: async (taskId: string) => {
    set({ loading: true, error: null });
    try {
      const task = await apiService.getTask(taskId);
      set({ selectedTask: task, loading: false });
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to fetch task',
        loading: false
      });
    }
  },

  fetchAgents: async (taskId: string) => {
    set({ loading: true, error: null });
    try {
      const agents = await apiService.getAgents(taskId);
      set({ agents, loading: false });
    } catch (error) {
      set({
        error: error instanceof Error ? error.message : 'Failed to fetch agents',
        loading: false
      });
    }
  },

  fetchFindings: async (taskId: string) => {
    try {
      const findings = await apiService.getFindings(taskId);
      set({ findings });
    } catch (error) {
      console.error('Failed to fetch findings:', error);
    }
  },

  selectTask: (task: Task | null) => {
    set({ selectedTask: task, selectedAgent: null });
    if (task) {
      // Fetch related data when a task is selected
      get().fetchAgents(task.task_id);
      get().fetchFindings(task.task_id);
    }
  },

  selectAgent: (agent: Agent | null) => {
    set({ selectedAgent: agent });
  },

  addLog: (log: LogMessage) => {
    set((state) => ({
      logs: [...state.logs, log].slice(-1000) // Keep last 1000 logs
    }));
  },

  clearLogs: () => {
    set({ logs: [] });
  },

  setError: (error: string | null) => {
    set({ error });
  }
}));

