// Task phase states matching the orchestrator's phase state machine
export type PhaseStatus =
  | 'PENDING'
  | 'ACTIVE'
  | 'AWAITING_REVIEW'
  | 'UNDER_REVIEW'
  | 'APPROVED'
  | 'REJECTED'
  | 'REVISING'
  | 'ESCALATED';

export type AgentStatus =
  | 'working'
  | 'blocked'
  | 'completed'
  | 'error'
  | 'failed'
  | 'terminated';

export interface Phase {
  name: string;
  status: PhaseStatus;
  description?: string;
  agents?: string[];
  completion?: {
    all_agents_done: boolean;
    total_agents: number;
    completed_agents: number;
    pending_agents: string[];
    ready_for_review: boolean;
  };
}

export interface Agent {
  id: string;
  type: string;
  tmux_session: string;
  parent: string;
  depth: number;
  phase_index: number;
  status: AgentStatus;
  started_at: string;
  progress: number;
  last_update: string;
  prompt?: string;
  tracked_files?: {
    prompt_file: string;
    log_file: string;
    progress_file: string;
    findings_file: string;
    deploy_log: string;
  };
  failed_at?: string;
  failure_reason?: string;
  completed_at?: string;
  final_verdict?: string;
  completion_validation?: {
    confidence: number;
    warnings: string[];
    blocking_issues: string[];
  };
}

export interface Task {
  task_id: string;
  description: string;
  status: string;
  workspace: string;
  phases: {
    total: number;
    current_index: number;
    current_phase: string;
    current_status: PhaseStatus;
    completion?: {
      all_agents_done: boolean;
      total_agents: number;
      completed_agents: number;
      pending_agents: string[];
      ready_for_review: boolean;
    };
    all_phases: Phase[];
  };
  agents?: {
    total_spawned: number;
    active: number;
    completed: number;
    agents_list: Agent[];
  };
  hierarchy?: Record<string, string[]>;
  enhanced_progress?: {
    recent_updates: Array<{
      timestamp: string;
      agent_id: string;
      status: string;
      message: string;
      progress: number;
    }>;
    recent_findings: Array<{
      timestamp: string;
      agent_id: string;
      finding_type: string;
      severity: string;
      message: string;
      data?: Record<string, unknown>;
    }>;
    total_progress_entries: number;
    total_findings: number;
    progress_frequency: number;
  };
}

export interface TaskSummary {
  task_id: string;
  description: string;
  status: string;
  current_phase: string;
  phase_status: PhaseStatus;
  agent_count: {
    active: number;
    total: number;
  };
  created_at?: string;
}

